import { Button, TextField } from "@material-ui/core";
import { Form, Formik, Field } from "formik";
import * as React from "react";
import { MyField } from "./MyField";


const initialValues: Values = {
    firstName: "", lastName: "", email: "", paso: 0, 
	arr: [{
		label1: [{"display":"none"}],
		label2: "hide",
		label3: "display:none",
		label4: "display:none",
		label5: "display:none",
		label6: "display:none",
		label7: "display:none",
		label8: "display:none",
		label9: "display:none",
		label10: "display:none",
		label11: "display:none"
	}]
};
export interface Stl {
		propi:string;
		valor:string;
	}

export interface Values {
  firstName: string;
  lastName: string;
  email: string;
  paso: number;
    arr: {
		label1: Array<{stl}>;
		label2: string;
		label3: string;
		label4: string;
		label5: string;
		label6: string;
		label7: string;
		label8: string;
		label9: string;
		label10: string;
		label11: string;
    }
}
/*
interface defItem {
  [index: string]: defItemDeta;
}
interface defItemDeta {
  label1: string;
  label2: string;
  label3: string;
  label4: string;
  label5: string;
  label6: string;
  label7: string;
  label8: string;
  label9: string;
  label10: string;
  label11: string;
}*/

interface Props {
  onSubmit: (values: Values) => void;
}

export const MyForm: React.FC<Props> = ({ onSubmit }) => {
  return (
    <Formik
      initialValues={initialValues}
      onSubmit={values => {
        onSubmit(values);
      }}
    >
      {({ values }) => (              
        <Form>
			<div id="x" >
				test
			</div>
          <div>
            <Field
              name="firstName"
              placeholder="firsts name"
              
              component={MyField}
            />
          </div>
          <div>
            <Field
              name="lastName"
              placeholder="last name"
              
              component={MyField}
            />
          </div>
          <div>
            <Field name="email" 
			  placeholder="email" 
			  style={values.arr[values.paso].label1}
              component={MyField} />
          </div>
          <div>
            <Field name="paso" 
			  placeholder="paso" 
			  
              component={MyField} />
          </div>
          <Button type="submit">submit</Button>
          <pre>{JSON.stringify(values, null, 2)}</pre>
        </Form>
      )}
    </Formik>
  );
};
