const depth = {
  inset: {
    boxShadow: 'inset 0px 0px 8px 0 rgba(82, 97, 115, 0.09)' as 'inset 0px 0px 8px 0 rgba(82, 97, 115, 0.09)',
  },
  1: { boxShadow: '0px 2px 4px 0 rgba(0, 0, 0, 0.08)' as '0px 2px 4px 0 rgba(0, 0, 0, 0.08)' },
  2: { boxShadow: '0px 4px 8px 0 rgba(0, 0, 0, 0.1)' as '0px 4px 8px 0 rgba(0, 0, 0, 0.1)' },
  3: { boxShadow: '0px 8px 16px 0 rgba(0, 0, 0, 0.12)' as '0px 8px 16px 0 rgba(0, 0, 0, 0.12)' },
  4: {
    border: '1px solid rgba(218, 226, 230, 1)' as '1px solid rgba(218, 226, 230, 1)',
    boxShadow: '0px 8px 16px 0 rgba(0, 0, 0, 0.12)' as '0px 8px 16px 0 rgba(0, 0, 0, 0.12)',
  },
}

export type CanvasDepthValue =
  | typeof depth.inset
  | typeof depth[1]
  | typeof depth[2]
  | typeof depth[3]
  | typeof depth[4]

export type CanvasDepth = typeof depth

